import Input from "./Input";
import TextArea from "./TextArea";

class Form extends Component {
  render() {

    return (
      <div>
        <h1>Formulario</h1>
        <Input inputLabel='Texto1' tipo="textarea" testeId='name-input'/>
        <Input inputLabel='numero' tipo="number" testeId='name-input'/>
        <TextArea testeId='description-input' inputLabel='text area' />

        </div>
    )
  }
}

export default Form
